import React, {useState} from 'react';
import { Link } from 'react-router-dom';
import { Menu,  Switch } from 'antd';

import './index.css';



const Nav = () => {

    const [theme, setTheme] = useState(null);

    const { SubMenu } = Menu;

    const dark = (value: any) => {

               setTheme(value  ? 'dark' : 'light');    
               const menu = document.getElementsByClassName('ant-menu')[0] as HTMLElement ;

               const body = document.body;
               body.classList.toggle('dark');
       };

    return (
        <div className="nav">  

                 <Menu mode="horizontal" className="menu" theme={theme}>

                       <SubMenu
                             title={

                                <Switch
                                 onChange={dark}
                                 checkedChildren="Dark"
                                 unCheckedChildren="Light"
                               /> 

                          }
                        >  
                        </SubMenu>

                        <SubMenu
                          title={
                            <span className="submenu-title-wrapper">
                                La société
                            </span>
                          }
                        > 
                          <Menu.ItemGroup >
                            <Menu.Item key="pres"><Link to="/presentation"> Présentation </Link ></Menu.Item>
                            <Menu.Item key="part"><Link to="/partenaires"> Les partenaires </Link ></Menu.Item>
                          </Menu.ItemGroup>
                        </SubMenu>

                        <SubMenu
                          title={
                            <span className="submenu-title-wrapper">
                              Luminaires
                            </span>
                          }
                        >
                          <Menu.ItemGroup >
                            <Menu.Item key="lamp"><Link to="/lamp"> Lampes </Link ></Menu.Item>
                            <Menu.Item key="chand"><Link to="/chandelier"> Lustres </Link ></Menu.Item>
                            <Menu.Item key="wall"><Link to="/wall-light"> Appliques </Link ></Menu.Item>
                            <Menu.Item key="lamppost"><Link to="/lamppost"> Lampadaires </Link ></Menu.Item>
                            <Menu.Item key="spot"><Link to="/flight-spot"> Spot </Link ></Menu.Item>
                          </Menu.ItemGroup>
                        </SubMenu>

                        <Menu.Item key="actu">
                         <Link to="/actualite"> Actualités </Link >
                        </Menu.Item>

                        <Menu.Item key="ref">
                          <Link to="/references"> Références </Link>
                        </Menu.Item>


                        <Menu.Item key="contact">
                         <Link to="/contact"> Contact </Link >
                        </Menu.Item>

                 </Menu>
        </div>
    )
}

export default Nav;
